// 6) Defininiciones SQL
const SQL_BY_MONTH = "DATE_FORMAT(fecha, '%Y-%m')";
const SQL_BY_DAY Â  = "DATE_FORMAT(fecha, '%Y-%m-%d')";
const SQL_BY_HOUR Â = "DATE_FORMAT(fecha, '%Y-%m-%d %H:00:00')";

// FunciÃ³n fmt(d):
// Siempre devuelve "YYYY-MM-DD" en UTC
function fmt(d) {
Â  return d.toISOString().slice(0, 10);
}

try {
Â  // â”€â”€â”€ Normalizo si me llega un objeto Date â”€â”€â”€
Â  // Esta parte maneja casos donde msg.payload podrÃ­a ser un objeto Date en lugar de string ISO
 Â if (payload instanceof Date) {
Â  Â  const y = payload.getUTCFullYear();
Â  Â  const m = payload.getUTCMonth() + 1; // getUTCMonth es 0-indexed, add 1
Â  Â  const d = payload.getUTCDate();
Â  Â  if (period === 'month') {
Â  Â  Â  // Si es un Date y period 'month', lo convierte a "YYYY-MM" string.
      // NOTA: La compensaciÃ³n de 1 mes se aplica mÃ¡s abajo para el string "YYYY-MM"
Â  Â  Â  msg.payload = `${y}-${String(m).padStart(2,'0')}`;
Â  Â  }
Â  Â  else if (period === 'week' || period === 'day') {
Â  Â  Â  // Si es un Date y period 'week' o 'day', lo convierte a "YYYY-MM-DD" string.
      // NOTA: La compensaciÃ³n de 1 dÃ­a se aplica mÃ¡s abajo para el string "YYYY-MM-DD"
Â  Â  Â  msg.payload = `${y}-${String(m).padStart(2,'0')}-${String(d).padStart(2,'0')}`;
Â  Â  }
Â  Â  // Actualizamos la variable local 'payload' con el string formateado para el switch()
Â  Â  payload = msg.payload;
Â  }


Â  let startDate, endDate, groupBy;

Â  switch (period) {

Â  Â  case 'year': {
Â  Â  Â  const raw = String(payload); // payload esperado: "YYYY-MM-DD" (aunque solo se usa YYYY)
Â  Â  Â  const y = parseInt(raw.split('-')[0], 10);
Â  Â  Â  if (isNaN(y)) throw new Error(`AÃ±o invÃ¡lido: ${payload}`);
Â  Â  Â  startDate = new Date(Date.UTC(y, 0, 1));
Â  Â  Â  endDate Â  = new Date(Date.UTC(y, 11, 31, 23, 59, 59));
Â  Â  Â  groupBy Â  = SQL_BY_MONTH;
Â  Â  Â  break;
Â  Â  }

Â  Â  case 'month': {
Â  Â  Â  // Asumimos que el payload "YYYY-MM" es 1 mes ANTES del mes deseado.
Â  Â  Â  const [y_in, m_in] = String(payload).split('-').map(n => parseInt(n, 10));
Â  Â  Â  if (isNaN(y_in) || isNaN(m_in)) throw new Error(`Mes invÃ¡lido: ${payload}`);

Â  Â  Â  let m_intended = m_in + 1;
Â  Â  Â  let y_intended = y_in;

Â  Â  Â  if (m_intended > 12) {
Â  Â  Â  Â  m_intended = 1;
Â  Â  Â  Â  y_intended += 1;
Â  Â  Â  }

Â  Â  Â  startDate = new Date(Date.UTC(y_intended, m_intended - 1, 1));
Â  Â  Â  endDate Â  = new Date(Date.UTC(y_intended, m_intended, 0, 23, 59, 59));

Â  Â  Â  groupBy Â  = SQL_BY_DAY;
Â  Â  Â  break;
Â  Â  }

Â  Â  case 'week': {
Â  Â  Â  const [y, m, d] = String(payload).split('-').map(n => parseInt(n, 10));
Â  Â  Â  if (isNaN(y) || isNaN(m) || isNaN(d)) throw new Error(`Fecha invÃ¡lida: ${payload}`);
Â  Â  Â  const base = new Date(Date.UTC(y, m - 1, d)); // Crea una fecha en UTC para el dÃ­a recibido
Â  Â  Â  const dow Â = base.getUTCDay(); // DÃ­a de la semana UTC (0=Dom, 1=Lun...)
Â  Â  Â  // Calcula la diferencia para ir al lunes de esa semana (lunes es dÃ­a 1, domingo es 0 aquÃ­).
Â  Â  Â  // Si dow es 0 (domingo), diff = -6 para ir al lunes anterior.
Â  Â  Â  // Si dow es 1 (lunes), diff = 1 - 1 = 0.
Â  Â  Â  // Si dow es 6 (sÃ¡bado), diff = 1 - 6 = -5 (para ir al lunes).
Â  Â  Â  const diff = dow === 0 ? -6 : 1 - dow;

Â  Â  Â  startDate = new Date(base); // Copia la fecha base
Â  Â  Â  startDate.setUTCDate(base.getUTCDate() + diff); // Ajusta al lunes de la semana en UTC
Â  Â  Â  endDate Â  = new Date(startDate); // Copia el lunes
Â  Â  Â  endDate.setUTCDate(startDate.getUTCDate() + 6); // Avanza 6 dÃ­as para llegar al domingo en UTC
Â  Â  Â  groupBy Â  = SQL_BY_DAY;
Â  Â  Â  break;
Â  Â  }

Â  Â  case 'day': {
Â  Â  Â  const [Y_in, M_in, D_in] = String(payload).split('-').map(n => parseInt(n, 10));
Â  Â  Â  if (isNaN(Y_in) || isNaN(M_in) || isNaN(D_in)) throw new Error(`Fecha invÃ¡lida: ${payload}`);

Â  Â  Â  // --- INICIO DEL CAMBIO ---
Â  Â  Â  // Asumimos que el dÃ­a recibido (Y_in-M_in-D_in) es 1 dÃ­a ANTES del dÃ­a deseado.
Â  Â  Â  // Creamos un objeto Date en UTC para el dÃ­a recibido.
Â  Â  Â  let receivedDate = new Date(Date.UTC(Y_in, M_in - 1, D_in)); // M_in es 1-indexed, M_in-1 es 0-indexed mes

Â  Â  Â  // AÃ±adimos 1 dÃ­a a este objeto Date. setUTCDate maneja automÃ¡ticamente los cambios de mes y aÃ±o en UTC.
Â  Â  Â  receivedDate.setUTCDate(receivedDate.getUTCDate() + 1);

Â  Â  Â  // Formateamos la fecha CORREGIDA de vuelta a string YYYY-MM-DD
Â  Â  Â  const correctedDayStr = fmt(receivedDate);
Â  Â  Â  // --- FIN DEL CAMBIO ---

Â  Â  Â  msg.topic =
Â  Â  Â  Â  `SELECT
Â  Â  Â  Â  Â  Â ${SQL_BY_HOUR} AS periodo,
Â  Â  Â  Â  Â  Â SUM(Radiacion_Solar) AS Radiacion_Solar,
Â  Â  Â  Â  Â  Â SUM(Temperatura) Â  Â  AS Temperatura
Â  Â  Â  Â  Â FROM Mediciones_Sensores
Â  Â  Â  Â  Â WHERE fecha BETWEEN '${correctedDayStr} 00:00:00' AND '${correctedDayStr} 23:59:59'
Â  Â  Â  Â  Â GROUP BY 1
Â  Â  Â  Â  Â ORDER BY 1`
Â  Â  Â  Â  .replace(/\s+/g,' ').trim();

Â  Â  Â  msg.selected_period = 'day';
Â  Â  Â  // Usamos el dÃ­a corregido para startDate y endDate en el msg de salida
Â  Â  Â  msg.startDate Â  Â  Â  = correctedDayStr;
Â  Â  Â  msg.endDate Â  Â  Â  Â  = correctedDayStr;
Â  Â  Â  delete msg.payload; // El payload original (probablemente incorrecto) no se envÃ­a al siguiente nodo
Â  Â  Â  return msg; // Retorna directamente para el caso 'day' ya que la consulta se genera aquÃ­
Â  Â  }

Â  Â  default:
Â  Â  Â  throw new Error(`Unknown period: ${period}`);
Â  }

Â  // Para 'year', 'month' o 'week'
Â  // La consulta se genera despuÃ©s del switch, usando startDate y endDate calculados arriba
Â  msg.topic =
Â  Â  `SELECT
Â  Â  Â  Â ${groupBy} AS periodo,
Â  Â  Â  Â SUM(Radiacion_Solar) AS Radiacion_Solar,
Â  Â  Â  Â SUM(Temperatura) Â  Â  AS Temperatura
Â  Â  Â FROM Mediciones_Sensores
Â  Â  Â WHERE fecha BETWEEN '${fmt(startDate)} 00:00:00' AND '${fmt(endDate)} 23:59:59'
Â  Â  Â GROUP BY 1
Â  Â  Â ORDER BY 1`
Â  Â  .replace(/\s+/g,' ').trim();

Â  msg.selected_period = period;
Â  msg.startDate Â  Â  Â  = fmt(startDate); // Aseguramos que el output msg.startDate/endDate sea YYYY-MM-DD del rango calculado
Â  msg.endDate Â  Â  Â  Â  = fmt(endDate);
Â  delete msg.payload;
Â  return msg;

} catch (err) {
Â  node.error(`SQL Gen Error: ${err.message}`, msg);
Â  return null;
}

// Nodo: ConversiÃ³n a Chart
// Entrada: msg.payload = array de filas [{periodo, Radiacion_Solar, Temperatura}, â€¦]
//          msg.selected_period ('year'|'month'|'week'|'day')
//          msg.startDate, msg.endDate ("YYYY-MM-DD") - Representan el rango REAL del periodo
// Salida: msg.payload = [ { series: [...], data: [...], labels: [...] } ]

const rows = Array.isArray(msg.payload) ? msg.payload : [];
const periodType = msg.selected_period;
const startDate = msg.startDate;
const endDate = msg.endDate;

// Verificar que tenemos datos necesarios
if (!periodType || !startDate || !endDate) {
    node.error("Faltan datos para la conversiÃ³n a Chart (periodType, startDate, endDate).", msg);
    return null;
}

// Genera array completo de periodos/etiquetas esperados para el eje X
function genPeriods(start, end, type) {
    let list = [];
    let s, e, cur;

    try {
        // 1) Parseamos las fechas en UTC aÃ±adiendo Z para asegurar UTC
        s = new Date(start + 'T00:00:00Z');
        e = new Date(end   + 'T00:00:00Z');
        if (isNaN(s) || isNaN(e)) throw new Error("Fechas de inicio/fin invÃ¡lidas.");
        cur = new Date(s); // Copia para iterar
    } catch (err) {
        node.error(`Error parseando fechas en genPeriods: ${err.message}`);
        return []; // Devuelve lista vacÃ­a si hay error
    }


    if (type === 'year') {
        // 12 meses del aÃ±o YYYY-MM
        const year = s.getUTCFullYear();
        for (let m = 0; m < 12; m++) {
            const ym = `${year}-${String(m + 1).padStart(2, '0')}`;
            list.push(ym);
        }
    }
    else if (type === 'month' || type === 'week') {
        // dÃ­as entre start y end (inclusive) YYYY-MM-DD
        while (cur <= e) {
            const d = [
                cur.getUTCFullYear(),
                String(cur.getUTCMonth() + 1).padStart(2, '0'),
                String(cur.getUTCDate()).padStart(2, '0')
            ].join('-');
            list.push(d);
            // Avanzar al siguiente dÃ­a UTC
            cur.setUTCDate(cur.getUTCDate() + 1);
            // Seguridad anti-bucle infinito (muy improbable con fechas vÃ¡lidas)
            if (list.length > 366) {
                node.warn("genPeriods excediÃ³ lÃ­mite de iteraciones (posible bucle)");
                break;
            }
        }
    }
    else if (type === 'day') {
        // 24 horas del dÃ­a YYYY-MM-DD HH:00:00
        const dayStr = start; // start es YYYY-MM-DD
        for (let h = 0; h < 24; h++) {
            const hh = String(h).padStart(2, '0');
            list.push(`${dayStr} ${hh}:00:00`);
        }
    }
    return list;
}

// Map de datos SQL para lookup rÃ¡pido usando 'periodo' como clave
const dataMap = {};
rows.forEach(r => {
    // Asegurarse que el periodo no sea null/undefined
    if (r && r.periodo !== null && r.periodo !== undefined) {
        dataMap[r.periodo] = {
            rs: r.Radiacion_Solar === null ? 0 : Number(r.Radiacion_Solar) || 0,
            t: r.Temperatura === null ? 0 : Number(r.Temperatura) || 0
        };
    } else {
        node.warn(`Fila SQL ignorada por periodo invÃ¡lido: ${JSON.stringify(r)}`);
    }
});

// Generamos todas las etiquetas posibles segÃºn el periodo y las fechas reales
const fullPeriods = genPeriods(startDate, endDate, periodType);

// Construimos el objeto que necesita el nodo ui_chart
let chart = {
    series: ["Radiacion_Solar", "Temperatura"], // Nombres de las series
    data: [[], []],                             // Arrays de datos, uno por serie
    labels: []                                  // Etiquetas del eje X
};

// Llenamos el chart con los datos, usando 0 si no hay datos para un periodo
fullPeriods.forEach(pv => {
    // Formateamos la etiqueta: Hora para 'day', fecha/mes para otros
    let label;
    if (periodType === 'day') {
        try {
             // Intenta crear una fecha (puede fallar si pv no es formato esperado)
             // Asumimos pv es 'YYYY-MM-DD HH:00:00'
            const dateObj = new Date(pv.replace(' ', 'T') + 'Z'); // Interpretar como UTC
             if (!isNaN(dateObj)) {
                // Usar toLocaleTimeString para formato local HH:MM
                // O especificar horas/minutos directamente si falla toLocaleTimeString
                 label = dateObj.toLocaleTimeString('es-ES', { hour: '2-digit', minute: '2-digit', hour12: false, timeZone: 'UTC' });
                 // Fallback por si toLocaleTimeString da problemas en algÃºn entorno
                 if (!label || label.includes("Invalid")) {
                    const h = dateObj.getUTCHours();
                    const min = dateObj.getUTCMinutes();
                    label = `${String(h).padStart(2,'0')}:${String(min).padStart(2,'0')}`;
                 }
             } else {
                 label = pv; // Usar el string original si falla la conversiÃ³n
             }
        } catch(e) {
            label = pv; // Fallback si hay error
        }
    } else {
        label = pv; // Para year, month, week, usar YYYY-MM o YYYY-MM-DD
    }
    chart.labels.push(label);

    const entry = dataMap[pv] || { rs: 0, t: 0 }; // Busca el dato, o usa {0, 0}
    chart.data[0].push(entry.rs);
    chart.data[1].push(entry.t);
});

// El nodo ui_chart espera un array que contenga el objeto chart
msg.payload = [chart];

// Limpiamos propiedades auxiliares que ya no necesitamos
delete msg.selected_period;
delete msg.startDate;
delete msg.endDate;
delete msg.topic; // El topic SQL ya no es necesario

return msg;
