}

return msg;

// Inicia la variable con la estructura básica del HTML y la tabla
var html = `<html>
  <head>
    <meta charset="UTF-8">
    <style>
      table { border-collapse: collapse; width: 100%; }
      th, td { border: 1px solid #ccc; padding: 8px; text-align: center; }
      th { background-color: #f2f2f2; }
    </style>
  </head>
  <body>
    <h2>Datos sensores MeteoCuerva</h2> <!-- Título dentro del cuerpo del email -->
    <table>
      <tr>
        <th>id</th>
        <th>fecha</th>
        <th>Temperatura</th>
        <th>Radiacion_Solar</th>
      </tr>`;

// Recorre cada registro obtenido de la consulta y agrega una fila a la tabla
msg.payload.forEach(function(row) {
    html += `<tr>
      <td>${row.id}</td>
      <td>${row.fecha}</td>
      <td>${row.Temperatura}</td>
      <td>${row.Radiacion_Solar}</td>

    </tr>`;
});

html += `</table>
  </body>
</html>`;

// Asigna el HTML generado al payload
msg.payload = html;

// Establece el asunto del correo
msg.topic = "Datos sensores Meteo Cuerva*";

return msg;

//msg.payload contiene los datos obtenidos de la base de datos
let data = msg.payload;

// Transformar los datos en un array de objetos
let excelData = data.map(row => ({
    Temperatura: row.Temperatura,
    Radiacion_Solar: row.Radiacion_Solar
}));

msg.payload = excelData;
// Establece el asunto del correo
msg.topic = "Datos sensores Meteo Cuerva*";
msg.filename = "Mediciones_sensores_METEOCUERVA*.xlsx";
return msg;

// Obtener fechas del query (ej: ?inicio=2025-04-22T00:00:00)
const fechaInicio = msg.req.query.inicio.replace("T", " ");  // Formato: '2025-04-22 00:00:00'
const fechaFin = msg.req.query.fin.replace("T", " ");        // Formato: '2025-04-22 23:59:59'

// Construir consulta SIN parámetros (como en tu ejemplo funcional)
msg.topic = `
  SELECT fecha, Radiacion_Solar, Temperatura
    FROM Mediciones_Sensores
   WHERE fecha >= '${fechaInicio}'
     AND fecha <= '${fechaFin}'
   ORDER BY fecha
`;

return msg;

// msg.payload: array de objetos con { fecha, Radiacion_Solar, Temperatura }
let data = msg.payload;

let excelData = data.map(row => {
    // parsear la fecha ISO a objeto Date
    const d = new Date(row.fecha);
    // formatear a “dd/mm/yyyy hh:MM:ss”
    const fechaHora = d.toLocaleString('es-ES', {
        day: '2-digit',
        month: '2-digit',
        year: 'numeric',
        hour: '2-digit',
        minute: '2-digit',
        second: '2-digit',
        hour12: false
    });
    return {
        FechaHora: fechaHora,
        Temperatura: row.Temperatura,
        Radiacion_Solar: row.Radiacion_Solar
    };
});

msg.payload = excelData;
msg.topic = "Datos sensores Meteo Cuerva";
msg.filename = "Datos_FOTOV_UNI_MADRID.xlsx";
return msg;

// Obtener fechas desde los parámetros
const fechaInicio = msg.req.query.inicio.split('T')[0]; // Ej: "2025-04-22"
const fechaFin = msg.req.query.fin.split('T')[0];

// Configurar headers para forzar la descarga como .xlsx
msg.headers = {
  "Content-Type": "application/vnd.openxmlformats-officedocument.spreadsheetml.sheet", // MIME type correcto
  "Content-Disposition": `attachment; filename="Datos_Fotov_UNI_MADRID_${fechaInicio}_a_${fechaFin}.xlsx"` // Extensión explícita
};

return msg;

// ─── Nodo: SQL Gen (polling revisado con parsing de fecha ISO para “year”|“month”|“week”|“day”) ───

// 1) Recuperar últimos settings del flujo
let settings = flow.get('lastSettings');

// 2) Si viene del date-picker: guardamos payload + period
if (msg.period !== undefined) {
  settings = { payload: msg.payload, period: msg.period };
  flow.set('lastSettings', settings);
}
// 3) Si viene del polling (msg.trigger) y no hay settings: salir
else if (msg.trigger === true) {
  if (!settings) {
    node.warn("Polling ignorado: sin selección previa");
    return null;
  }
}
// 4) Si no es ni uno ni otro: descartamos
else {
  return null;
}

// 5) Asignamos msg.payload y msg.period desde settings
let payload = settings.payload;
let period  = settings.period;
msg.payload = payload;
msg.period  = period;

// 6) Defininiciones SQL
const SQL_BY_MONTH = "DATE_FORMAT(fecha, '%Y-%m')";
const SQL_BY_DAY   = "DATE_FORMAT(fecha, '%Y-%m-%d')";
const SQL_BY_HOUR  = "DATE_FORMAT(fecha, '%Y-%m-%d %H:00:00')";

// Función fmt(d):
// Siempre devuelve "YYYY-MM-DD" en UTC
function fmt(d) {
  return d.toISOString().slice(0, 10);
}

try {
  // ─── Normalizo si me llega un objeto Date ───
  // Esta parte maneja casos donde msg.payload podría ser un objeto Date en lugar de string ISO
  if (payload instanceof Date) {
    const y = payload.getUTCFullYear();
    const m = payload.getUTCMonth() + 1; // getUTCMonth es 0-indexed, add 1
    const d = payload.getUTCDate();
    if (period === 'month') {
      // Si es un Date y period 'month', lo convierte a "YYYY-MM" string.
      // NOTA: La compensación de 1 mes se aplica más abajo para el string "YYYY-MM"
      msg.payload = `${y}-${String(m).padStart(2,'0')}`;
    }
    else if (period === 'week' || period === 'day') {
      // Si es un Date y period 'week' o 'day', lo convierte a "YYYY-MM-DD" string.
      // NOTA: La compensación de 1 día se aplica más abajo para el string "YYYY-MM-DD"
      msg.payload = `${y}-${String(m).padStart(2,'0')}-${String(d).padStart(2,'0')}`;
    }
    // Actualizamos la variable local 'payload' con el string formateado para el switch()
    payload = msg.payload;
  }


  let startDate, endDate, groupBy;

  switch (period) {

    case 'year': {
      const raw = String(payload); // payload esperado: "YYYY-MM-DD" (aunque solo se usa YYYY)
      const y = parseInt(raw.split('-')[0], 10);
      if (isNaN(y)) throw new Error(`Año inválido: ${payload}`);
      startDate = new Date(Date.UTC(y, 0, 1));
      endDate   = new Date(Date.UTC(y, 11, 31, 23, 59, 59));
      groupBy   = SQL_BY_MONTH;
      break;
    }

    case 'month': {
      // Asumimos que el payload "YYYY-MM" es 1 mes ANTES del mes deseado.
      const [y_in, m_in] = String(payload).split('-').map(n => parseInt(n, 10));
      if (isNaN(y_in) || isNaN(m_in)) throw new Error(`Mes inválido: ${payload}`);

      let m_intended = m_in + 1;
      let y_intended = y_in;

      if (m_intended > 12) {
        m_intended = 1;
        y_intended += 1;
      }

      startDate = new Date(Date.UTC(y_intended, m_intended - 1, 1));
      endDate   = new Date(Date.UTC(y_intended, m_intended, 0, 23, 59, 59));

      groupBy   = SQL_BY_DAY;
      break;
    }

    case 'week': {
      const [y, m, d] = String(payload).split('-').map(n => parseInt(n, 10));
      if (isNaN(y) || isNaN(m) || isNaN(d)) throw new Error(`Fecha inválida: ${payload}`);
      const base = new Date(Date.UTC(y, m - 1, d)); // Crea una fecha en UTC para el día recibido
      const dow  = base.getUTCDay(); // Día de la semana UTC (0=Dom, 1=Lun...)
      // Calcula la diferencia para ir al lunes de esa semana (lunes es día 1, domingo es 0 aquí).
      // Si dow es 0 (domingo), diff = -6 para ir al lunes anterior.
      // Si dow es 1 (lunes), diff = 1 - 1 = 0.
      // Si dow es 6 (sábado), diff = 1 - 6 = -5 (para ir al lunes).
      const diff = dow === 0 ? -6 : 1 - dow;

      startDate = new Date(base); // Copia la fecha base
      startDate.setUTCDate(base.getUTCDate() + diff); // Ajusta al lunes de la semana en UTC
      endDate   = new Date(startDate); // Copia el lunes
      endDate.setUTCDate(startDate.getUTCDate() + 6); // Avanza 6 días para llegar al domingo en UTC
      groupBy   = SQL_BY_DAY;
      break;
    }

    case 'day': {
      const [Y_in, M_in, D_in] = String(payload).split('-').map(n => parseInt(n, 10));
      if (isNaN(Y_in) || isNaN(M_in) || isNaN(D_in)) throw new Error(`Fecha inválida: ${payload}`);

      // --- INICIO DEL CAMBIO ---
      // Asumimos que el día recibido (Y_in-M_in-D_in) es 1 día ANTES del día deseado.
      // Creamos un objeto Date en UTC para el día recibido.
      let receivedDate = new Date(Date.UTC(Y_in, M_in - 1, D_in)); // M_in es 1-indexed, M_in-1 es 0-indexed mes

      // Añadimos 1 día a este objeto Date. setUTCDate maneja automáticamente los cambios de mes y año en UTC.
      receivedDate.setUTCDate(receivedDate.getUTCDate() + 1);

      // Formateamos la fecha CORREGIDA de vuelta a string YYYY-MM-DD
      const correctedDayStr = fmt(receivedDate);
      // --- FIN DEL CAMBIO ---

      msg.topic =
        `SELECT
           ${SQL_BY_HOUR} AS periodo,
           SUM(Radiacion_Solar) AS Radiacion_Solar,
           SUM(Temperatura)     AS Temperatura
         FROM Mediciones_Sensores
         WHERE fecha BETWEEN '${correctedDayStr} 00:00:00' AND '${correctedDayStr} 23:59:59'
         GROUP BY 1
         ORDER BY 1`
        .replace(/\s+/g,' ').trim();

      msg.selected_period = 'day';
      // Usamos el día corregido para startDate y endDate en el msg de salida
      msg.startDate       = correctedDayStr;
      msg.endDate         = correctedDayStr;
      delete msg.payload; // El payload original (probablemente incorrecto) no se envía al siguiente nodo
      return msg; // Retorna directamente para el caso 'day' ya que la consulta se genera aquí
    }

    default:
      throw new Error(`Unknown period: ${period}`);
  }

  // Para 'year', 'month' o 'week'
  // La consulta se genera después del switch, usando startDate y endDate calculados arriba
  msg.topic =
    `SELECT
       ${groupBy} AS periodo,
       SUM(Radiacion_Solar) AS Radiacion_Solar,
       SUM(Temperatura)     AS Temperatura
     FROM Mediciones_Sensores
     WHERE fecha BETWEEN '${fmt(startDate)} 00:00:00' AND '${fmt(endDate)} 23:59:59'
     GROUP BY 1
     ORDER BY 1`
    .replace(/\s+/g,' ').trim();

  msg.selected_period = period;
  msg.startDate       = fmt(startDate); // Aseguramos que el output msg.startDate/endDate sea YYYY-MM-DD del rango calculado
  msg.endDate         = fmt(endDate);
  delete msg.payload;
  return msg;

} catch (err) {
  node.error(`SQL Gen Error: ${err.message}`, msg);
  return null;
}

// Nodo: Conversión a Chart
// Entrada: msg.payload = array de filas [{periodo, Radiacion_Solar, Temperatura}, …]
//          msg.selected_period ('year'|'month'|'week'|'day')
//          msg.startDate, msg.endDate ("YYYY-MM-DD") - Representan el rango REAL del periodo
// Salida: msg.payload = [ { series: [...], data: [...], labels: [...] } ]

const rows = Array.isArray(msg.payload) ? msg.payload : [];
const periodType = msg.selected_period;
const startDate = msg.startDate;
const endDate = msg.endDate;

// Verificar que tenemos datos necesarios
if (!periodType || !startDate || !endDate) {
    node.error("Faltan datos para la conversión a Chart (periodType, startDate, endDate).", msg);
    return null;
}

// Genera array completo de periodos/etiquetas esperados para el eje X
function genPeriods(start, end, type) {
    let list = [];
    let s, e, cur;

    try {
        // 1) Parseamos las fechas en UTC añadiendo Z para asegurar UTC
        s = new Date(start + 'T00:00:00Z');
        e = new Date(end   + 'T00:00:00Z');
        if (isNaN(s) || isNaN(e)) throw new Error("Fechas de inicio/fin inválidas.");
        cur = new Date(s); // Copia para iterar
    } catch (err) {
        node.error(`Error parseando fechas en genPeriods: ${err.message}`);
        return []; // Devuelve lista vacía si hay error
    }


    if (type === 'year') {
        // 12 meses del año YYYY-MM
        const year = s.getUTCFullYear();
        for (let m = 0; m < 12; m++) {
            const ym = `${year}-${String(m + 1).padStart(2, '0')}`;
            list.push(ym);
        }
    }
    else if (type === 'month' || type === 'week') {
        // días entre start y end (inclusive) YYYY-MM-DD
        while (cur <= e) {
            const d = [
                cur.getUTCFullYear(),
                String(cur.getUTCMonth() + 1).padStart(2, '0'),
                String(cur.getUTCDate()).padStart(2, '0')
            ].join('-');
            list.push(d);
            // Avanzar al siguiente día UTC
            cur.setUTCDate(cur.getUTCDate() + 1);
            // Seguridad anti-bucle infinito (muy improbable con fechas válidas)
            if (list.length > 366) {
                node.warn("genPeriods excedió límite de iteraciones (posible bucle)");
                break;
            }
        }
    }
    else if (type === 'day') {
        // 24 horas del día YYYY-MM-DD HH:00:00
        const dayStr = start; // start es YYYY-MM-DD
        for (let h = 0; h < 24; h++) {
            const hh = String(h).padStart(2, '0');
            list.push(`${dayStr} ${hh}:00:00`);
        }
    }
    return list;
}

// Map de datos SQL para lookup rápido usando 'periodo' como clave
const dataMap = {};
rows.forEach(r => {
    // Asegurarse que el periodo no sea null/undefined
    if (r && r.periodo !== null && r.periodo !== undefined) {
        dataMap[r.periodo] = {
            rs: r.Radiacion_Solar === null ? 0 : Number(r.Radiacion_Solar) || 0,
            t: r.Temperatura === null ? 0 : Number(r.Temperatura) || 0
        };
    } else {
        node.warn(`Fila SQL ignorada por periodo inválido: ${JSON.stringify(r)}`);
    }
});

// Generamos todas las etiquetas posibles según el periodo y las fechas reales
const fullPeriods = genPeriods(startDate, endDate, periodType);

// Construimos el objeto que necesita el nodo ui_chart
let chart = {
    series: ["Radiacion_Solar", "Temperatura"], // Nombres de las series
    data: [[], []],                             // Arrays de datos, uno por serie
    labels: []                                  // Etiquetas del eje X
};

// Llenamos el chart con los datos, usando 0 si no hay datos para un periodo
fullPeriods.forEach(pv => {
    // Formateamos la etiqueta: Hora para 'day', fecha/mes para otros
    let label;
    if (periodType === 'day') {
        try {
             // Intenta crear una fecha (puede fallar si pv no es formato esperado)
             // Asumimos pv es 'YYYY-MM-DD HH:00:00'
            const dateObj = new Date(pv.replace(' ', 'T') + 'Z'); // Interpretar como UTC
             if (!isNaN(dateObj)) {
                // Usar toLocaleTimeString para formato local HH:MM
                // O especificar horas/minutos directamente si falla toLocaleTimeString
                 label = dateObj.toLocaleTimeString('es-ES', { hour: '2-digit', minute: '2-digit', hour12: false, timeZone: 'UTC' });
                 // Fallback por si toLocaleTimeString da problemas en algún entorno
                 if (!label || label.includes("Invalid")) {
                    const h = dateObj.getUTCHours();
                    const min = dateObj.getUTCMinutes();
                    label = `${String(h).padStart(2,'0')}:${String(min).padStart(2,'0')}`;
                 }
             } else {
                 label = pv; // Usar el string original si falla la conversión
             }
        } catch(e) {
            label = pv; // Fallback si hay error
        }
    } else {
        label = pv; // Para year, month, week, usar YYYY-MM o YYYY-MM-DD
    }
    chart.labels.push(label);

    const entry = dataMap[pv] || { rs: 0, t: 0 }; // Busca el dato, o usa {0, 0}
    chart.data[0].push(entry.rs);
    chart.data[1].push(entry.t);
});

// El nodo ui_chart espera un array que contenga el objeto chart
msg.payload = [chart];

// Limpiamos propiedades auxiliares que ya no necesitamos
delete msg.selected_period;
delete msg.startDate;
delete msg.endDate;
delete msg.topic; // El topic SQL ya no es necesario

return msg;
