// FunciÃ³n para generar la lista completa de periodos entre startDate y endDate
function genPeriods(start, end, type) {
    const list = [];
    let current, startDt, endDt;

    try {
        startDt = new Date(start + 'T00:00:00Z');
        endDt = new Date(end + 'T00:00:00Z');
        if (isNaN(startDt) || isNaN(endDt)) throw new Error("Fechas invÃ¡lidas");
        current = new Date(startDt);
    } catch (err) {
        node.error(`Error al parsear fechas en genPeriods: ${err.message}`, msg);
        return list;
    }

    if (type === 'year') {
        // 12 meses del aÃ±o: YYYY-MM
        const year = startDt.getUTCFullYear();
        for (let m = 1; m <= 12; m++) {
            list.push(`${year}-${String(m).padStart(2, '0')}`);
        }

    } else if (type === 'month' || type === 'week') {
        // Cada dÃ­a entre start y end: YYYY-MM-DD
        while (current <= endDt) {
            const y = current.getUTCFullYear();
            const mo = String(current.getUTCMonth() + 1).padStart(2, '0');
            const da = String(current.getUTCDate()).padStart(2, '0');
            list.push(`${y}-${mo}-${da}`);
            current.setUTCDate(current.getUTCDate() + 1);
            if (list.length > 366) {
                node.warn("genPeriods: rango demasiado largo, se interrumpe bucle.");
                break;
            }
        }

    } else if (type === 'day') {
        // Cada hora del dÃ­a: "YYYY-MM-DD HH:00:00"
        for (let h = 0; h < 24; h++) {
            const hh = String(h).padStart(2, '0');
            list.push(`${start} ${hh}:00:00`);
        }
    }

    return list;
}

// Crear un map para acceso rÃ¡pido a los datos por periodo
const dataMap = {};
rows.forEach(row => {
    if (row && row.periodo != null) {
        dataMap[row.periodo] = {
            e_cons: Number(row.E_Consumido_Direct) || 0,
            e_sum: Number(row.E_Suministrada_RED) || 0
        };
    } else {
        node.warn(`Fila ignorada por periodo invÃ¡lido: ${JSON.stringify(row)}`);
