// ---------- Parámetros de configuración ----------
// msg.payload: [highWord, lowWord]
// msg.swap     (boolean) opcional; true si hay que invertir high/low.
// msg.scale    (number)  opcional; factor por el que dividimos el valor combinado.
// msg.decimals (number)  opcional; número de decimales en el resultado.

msg.swap = false;
msg.scale = 1000;    // kW en lugar de W
msg.decimals = 3;


const p = msg.payload;
if (!Array.isArray(p) || p.length !== 2
    || typeof p[0] !== "number"
    || typeof p[1] !== "number") {
    node.warn("Payload no es un array [high, low]");
    return null;
}

// Parámetros por defecto si no vienen en msg
const swap     = (typeof msg.swap     === "boolean") ? msg.swap     : false;
const scale    = (typeof msg.scale    === "number")  ? msg.scale    : 1;
const decimals = (typeof msg.decimals === "number")  ? msg.decimals : 0;

// Extraemos high y low
let high = p[0], low = p[1];

// Si hay que intercambiar palabras:
if (swap) {
    [high, low] = [low, high];
}

// Combinar en 32 bits sin signo
const combined = (high << 16) + low;

// Aplicar factor de escala y formatear
const value = combined / scale;
msg.payload = Number(value.toFixed(decimals));
msg.topic = "Potencia_Activa";

return msg;

