// Node-RED Function: construir consulta SQL para SUMA mensual (última lectura <= 22:30 por día)
// Entradas opcionales:
//   - msg.prevMonth = true    -> se usará el mes anterior
//   - msg.year (number)       -> año específico
//   - msg.month (number)      -> mes específico (1..12)
// Salida:
//   - msg.topic   : texto SQL
//   - msg.columns : nombres de columnas esperadas en el mismo orden

// Determinar modo de fechas
let mode = 'current'; // default
if (msg.prevMonth) mode = 'prev';
if (typeof msg.year === 'number' && typeof msg.month === 'number') mode = 'specific';

let sqlRange = '';
if (mode === 'specific') {
  // month debe ser 1..12
  let y = Number(msg.year);
  let m = String(msg.month).padStart(2, '0');
  // Se usa BETWEEN 'YYYY-MM-01' AND LAST_DAY('YYYY-MM-01')
  sqlRange = `fecha BETWEEN '${y}-${m}-01' AND LAST_DAY('${y}-${m}-01')`;
} else if (mode === 'prev') {
  sqlRange = `fecha BETWEEN DATE_FORMAT(CURDATE() - INTERVAL 1 MONTH, '%Y-%m-01')
                     AND LAST_DAY(CURDATE() - INTERVAL 1 MONTH)`;
} else {
  // mes actual
  sqlRange = `fecha BETWEEN DATE_FORMAT(CURDATE(), '%Y-%m-01') AND LAST_DAY(CURDATE())`;
}

// Construir la consulta que primero selecciona la última lectura por día (hora <= 22:30)
// y luego suma las columnas deseadas.
msg.topic = `
SELECT
  COALESCE(SUM(m.generacion_diaria),0) AS total_generacion,
  COALESCE(SUM(m.coste_fv_diario),0)   AS total_coste,
  COALESCE(SUM(m.consumida_real),0)    AS total_consumo
FROM Mediciones_Sensores m
INNER JOIN (
  SELECT DATE(fecha) AS dia, MAX(fecha) AS max_fecha
  FROM Mediciones_Sensores
  WHERE
    ${sqlRange}
    AND TIME(fecha) <= '22:30:00'
    AND generacion_diaria > 0
    AND coste_fv_diario > 0
    AND consumida_real > 0
  GROUP BY DATE(fecha)
) s ON DATE(m.fecha) = s.dia AND m.fecha = s.max_fecha;
`;

// Nombres de columnas (en el mismo orden que la SELECT)
msg.columns = ['total_generacion','total_coste','total_consumo'];

return msg;

