// Nodo Function: preparar email tras el Join (adjunta excel y deja el HTML en msg.payload)
// Entradas esperadas (varias opciones):
// - msg.payload = { html: "...", excel: <Buffer|ArrayBuffer|Uint8Array|base64 string> }
// - o msg.html, msg.excel por separado
// Opcionales:
// - msg.filename  -> nombre del fichero adjunto
// - msg.monthLabel -> "agosto 2025" (si quieres forzar etiqueta)
// - msg.emailSubject -> asunto personalizado

// --- 1) Obtener html y excel desde variantes posibles
let html = null;
let excelRaw = null;

if (msg.payload && typeof msg.payload === 'object') {
    if (msg.payload.html) html = msg.payload.html;
    if (msg.payload.excel) excelRaw = msg.payload.excel;
}
if (!html && msg.html) html = msg.html;
if (!excelRaw && msg.excel) excelRaw = msg.excel;

// Asegurarse de que html sea string
if (!html) html = '';

// --- 2) Normalizar excelRaw a Buffer si existe
let excelBuf = null;
if (excelRaw != null) {
    // Si viene como base64 string (posible "data:...;base64," o solo base64)
    if (typeof excelRaw === 'string') {
        let s = excelRaw;
        const m = s.match(/^data:.*;base64,(.*)$/);
        if (m) s = m[1];
        try {
            excelBuf = Buffer.from(s, 'base64');
        } catch (e) {
            // fallback: intentar convertir string directamente (si no es base64)
            excelBuf = Buffer.from(s);
        }
    } else {
        // Si viene como Buffer, ArrayBuffer, Uint8Array, etc.
        if (Buffer.isBuffer(excelRaw)) {
            excelBuf = excelRaw;
        } else if (excelRaw instanceof ArrayBuffer) {
            excelBuf = Buffer.from(new Uint8Array(excelRaw));
        } else if (ArrayBuffer.isView(excelRaw)) { // Uint8Array, etc.
            // excelRaw.buffer puede ser mayor que la vista (usar byteOffset/byteLength)
            excelBuf = Buffer.from(excelRaw.buffer, excelRaw.byteOffset || 0, excelRaw.byteLength || excelRaw.length);
        } else {
            // último recurso: intentar stringify
            try { excelBuf = Buffer.from(JSON.stringify(excelRaw)); } catch (e) { excelBuf = null; }
        }
    }
}

// --- 3) Determinar etiqueta de mes / sujeto
// Preferir msg.monthLabel si viene, si no intentar extraer de html o usar mes actual
let monthLabel = msg.monthLabel || null;
if (!monthLabel && typeof html === 'string') {
    // intentar detectar "Resumen Mensual: agosto 2025" u "Resumen Mensual FV: agosto 2025"
    const re = /Resumen Mensual[^:\n\r\-]*[:\-\s]*([A-Za-zñÑáéíóúÁÉÍÓÚ]+ \d{4})/i;
    const mm = html.match(re);
    if (mm && mm[1]) monthLabel = mm[1].toLowerCase();
}
if (!monthLabel) {
    monthLabel = new Intl.DateTimeFormat('es-ES', { month: 'long', year: 'numeric', timeZone: 'Europe/Madrid' }).format(new Date());
}

// Asunto: preferir msg.emailSubject si existe, si no elegir según tipo (mensual o genérico)
